import Vue from 'vue';
import VueRouter from 'vue-router';
import App from './App.vue';
import HelloWord from './components/HelloWord.vue';

Vue.use(VueRouter);

const router = new VueRouter({
    routes: [
        {
            path: '/cadastro',
            component: HelloWord
        }
    ]
})

Vue.config.productionTip = false;

new Vue({
    router,
    render: h => h(App),
}).$mount('#app')